import React, { useState } from 'react';

const CreateEvent = () => {
  const [spaceName, setSpaceName] = useState('');
  const [location, setLocation] = useState('');
  const [capacity, setCapacity] = useState('');
  const [amenities, setAmenities] = useState('');
  const [rate, setRate] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    // Add logic to handle form submission, e.g., sending data to a server
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <h2 className="text-2xl font-bold mb-4">Create a Space</h2>
      {!submitted ? (
        <form onSubmit={handleSubmit} className="max-w-md w-full">
          <div className="mb-4">
            <label htmlFor="spaceName" className="block text-gray-700 text-sm font-bold mb-2">
              Space Name
            </label>
            <input
              type="text"
              id="spaceName"
              value={spaceName}
              onChange={(e) => setSpaceName(e.target.value)}
              placeholder="Enter space name"
              className="px-3 py-2 placeholder-gray-400 text-gray-700 relative bg-white bg-white rounded text-sm shadow outline-none focus:outline-none focus:ring w-full"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="location" className="block text-gray-700 text-sm font-bold mb-2">
              Location
            </label>
            <input
              type="text"
              id="location"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              placeholder="Enter location"
              className="px-3 py-2 placeholder-gray-400 text-gray-700 relative bg-white bg-white rounded text-sm shadow outline-none focus:outline-none focus:ring w-full"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="capacity" className="block text-gray-700 text-sm font-bold mb-2">
              Capacity
            </label>
            <input
              type="text"
              id="capacity"
              value={capacity}
              onChange={(e) => setCapacity(e.target.value)}
              placeholder="Enter capacity"
              className="px-3 py-2 placeholder-gray-400 text-gray-700 relative bg-white bg-white rounded text-sm shadow outline-none focus:outline-none focus:ring w-full"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="amenities" className="block text-gray-700 text-sm font-bold mb-2">
              Amenities
            </label>
            <textarea
              id="amenities"
              value={amenities}
              onChange={(e) => setAmenities(e.target.value)}
              placeholder="Enter amenities"
              className="px-3 py-2 placeholder-gray-400 text-gray-700 relative bg-white bg-white rounded text-sm shadow outline-none focus:outline-none focus:ring w-full"
            ></textarea>
          </div>
          <div className="mb-4">
            <label htmlFor="rate" className="block text-gray-700 text-sm font-bold mb-2">
              Rate (per hour)
            </label>
            <input
              type="text"
              id="rate"
              value={rate}
              onChange={(e) => setRate(e.target.value)}
              placeholder="Enter rate"
              className="px-3 py-2 placeholder-gray-400 text-gray-700 relative bg-white bg-white rounded text-sm shadow outline-none focus:outline-none focus:ring w-full"
            />
          </div>
          <button
            type="submit"
            className="bg-indigo-500 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            Submit
          </button>
        </form>
      ) : (
        <div className="text-center">
          <p className="text-lg font-bold">Space created successfully!</p>
        </div>
      )}
    </div>
  );
};

export default CreateEvent;
