import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../../assets/images/logo.png'; // Adjust the path as needed
import backgroundImage from '../../assets/images/8448.jpg'; // Adjust the path as needed

const Spaces = () => {
  // Sample spaces data (replace with real data)
  const spaces = [
    {
      id: 1,
      image: 'https://via.placeholder.com/150', // Placeholder image
      name: 'Space One',
      location: 'Location One',
      capacity: '50',
      amenities: 'Wi-Fi, Projector',
      ratecard: '$200/hr'
    },
    {
      id: 2,
      image: 'https://via.placeholder.com/150', // Placeholder image
      name: 'Space Two',
      location: 'Location Two',
      capacity: '100',
      amenities: 'Wi-Fi, Sound System',
      ratecard: '$350/hr'
    }
    // Add more spaces as needed
  ];

  return (
    <div className="relative min-h-screen bg-cover bg-center" style={{ backgroundImage: `url(${backgroundImage})` }}>
      {/* Dark Overlay */}
      <div className="absolute inset-0 bg-black opacity-50"></div>

      {/* Top Navigation */}
      <header className="absolute top-0 left-0 w-full p-4 flex justify-between items-center z-10">
        <a href="/" className="flex items-center space-x-2">
          <img src={logo} alt="Space Hub Logo" className="w-20 h-18" />
        </a>
        <nav className="flex space-x-4">
          <Link to="/dashboard" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Back to Dashboard</Link>
          <button className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded">Logout</button>
        </nav>
      </header>

      {/* Main Content */}
      <div className="flex flex-col items-center justify-center min-h-screen text-center relative z-10 p-4">
        <h1 className="text-4xl font-bold text-white mb-12">Manage Spaces</h1>
        <div className="w-full max-w-4xl space-y-8">
          {spaces.map(space => (
            <div key={space.id} className="bg-white p-6 rounded-lg shadow-lg flex flex-col md:flex-row items-start md:items-center space-y-4 md:space-y-0 md:space-x-6">
              <img src={space.image} alt={space.name} className="w-full md:w-1/4 h-48 object-cover rounded-md" />
              <div className="flex-1">
                <h2 className="text-2xl font-bold mb-2">{space.name}</h2>
                <p className="text-lg mb-2"><strong>Location:</strong> {space.location}</p>
                <p className="text-lg mb-2"><strong>Capacity:</strong> {space.capacity}</p>
                <p className="text-lg mb-2"><strong>Amenities:</strong> {space.amenities}</p>
                <p className="text-lg mb-4"><strong>Rate Card:</strong> {space.ratecard}</p>
                  <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 m- rounded">Update</button>
                  <button className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded">Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Spaces;
