import React, { useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Signup() {
  const [message, setMessage] = useState(null);

  const formik = useFormik({
    initialValues: {
      email: '',
      password: '',
      confirmPassword: ''
    },
    validationSchema: Yup.object({
      email: Yup.string()
        .email('Invalid email')
        .required('Email is required'),
      password: Yup.string()
        .required('Password is required'),
      confirmPassword: Yup.string()
        .oneOf([Yup.ref('password'), null], 'Passwords must match')
        .required('Confirm Password is required')
    }),
    onSubmit: async (values) => {
      try {
        const response = await axios.post('/signup', values);
        if (response.status === 200) {
          setMessage('Signed up successfully');
          formik.resetForm();
        }
      } catch (error) {
        setMessage(error.response?.data?.message || 'Error signing up. Please try again.');
        setTimeout(() => {
          setMessage(null);
        }, 3000);
      }
    }
  });

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-blue-200 px-4 py-8">
      <div className="w-full max-w-lg bg-white shadow-lg rounded-lg px-8 pt-6 pb-8 mb-4 transition-transform transform hover:scale-105 duration-500 ease-in-out">
        <h2 className="text-3xl text-center font-bold mb-6">Sign Up</h2>
        {message && (
          <div
            className={
              message === 'Signed up successfully'
                ? 'text-green-500 text-center mb-4'
                : 'text-red-500 text-center mb-4'
            }
          >
            {message}
          </div>
        )}
        <form onSubmit={formik.handleSubmit}>
          <div className="mb-6">
            <input
              className="shadow-md appearance-none border border-gray-300 rounded w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:shadow-outline transition-transform transform hover:scale-105 duration-300"
              type="email"
              name="email"
              placeholder="Enter your email"
              onChange={formik.handleChange}
              value={formik.values.email}
            />
            {formik.touched.email && formik.errors.email && (
              <p className="text-red-500 mt-2">{formik.errors.email}</p>
            )}
          </div>
          <div className="mb-6 relative">
            <input
              className="shadow-md appearance-none border border-gray-300 rounded w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:shadow-outline transition-transform transform hover:scale-105 duration-300"
              type={formik.values.showPassword ? 'text' : 'password'}
              name="password"
              placeholder="Enter your password"
              onChange={formik.handleChange}
              value={formik.values.password}
            />
            {formik.touched.password && formik.errors.password && (
              <p className="text-red-500 mt-2">{formik.errors.password}</p>
            )}
          </div>
          <div className="mb-6 relative">
            <input
              className="shadow-md appearance-none border border-gray-300 rounded w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:shadow-outline transition-transform transform hover:scale-105 duration-300"
              type={formik.values.showPassword ? 'text' : 'password'}
              name="confirmPassword"
              placeholder="Confirm your password"
              onChange={formik.handleChange}
              value={formik.values.confirmPassword}
            />
            {formik.touched.confirmPassword && formik.errors.confirmPassword && (
              <p className="text-red-500 mt-2">{formik.errors.confirmPassword}</p>
            )}
          </div>
          <div className="flex items-center justify-center">
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded focus:outline-none focus:shadow-outline transition-transform transform hover:scale-105 duration-300"
            >
              Sign Up
            </button>
          </div>
        </form>
      </div>
      <p className="text-gray-600 text-lg">
        Already have an account? <Link to="/login" className="text-blue-500">Log In</Link>
      </p>
    </div>
  );
}

export default Signup;
