import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../../assets/images/logo.png'; // Adjust the path as needed
import backgroundImage from '../../assets/images/8448.jpg'; // Adjust the path as needed

const Spaces = () => {
  // Dummy data for spaces
  const spaces = [
    {
      id: 1,
      image: 'https://via.placeholder.com/150',
      name: 'Space 1',
      location: 'Location 1',
      capacity: '100 people',
      amenities: 'WiFi, Projector',
      ratecard: '$100/hr',
    },
    {
      id: 2,
      image: 'https://via.placeholder.com/150',
      name: 'Space 2',
      location: 'Location 2',
      capacity: '200 people',
      amenities: 'WiFi, Projector, Parking',
      ratecard: '$150/hr',
    },
    // Add more spaces as needed
  ];

  return (
    <div className="relative min-h-screen bg-cover bg-center" style={{ backgroundImage: `url(${backgroundImage})` }}>
      {/* Dark Overlay */}
      <div className="absolute inset-0 bg-black opacity-50"></div>

      {/* Top Navigation */}
      <header className="absolute top-0 left-0 w-full p-4 flex justify-between items-center z-10">
        <a href="/" className="flex items-center space-x-2">
          <img src={logo} alt="Space Hub Logo" className="w-20 h-18" />
        </a>
        <nav className="flex space-x-4">
          <Link to="/dashboard" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Back to Dashboard</Link>
          <button className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded">Logout</button>
        </nav>
      </header>

      {/* Main Content */}
      <div className="flex flex-col items-center justify-center min-h-screen text-center relative z-10 py-16">
        <h1 className="text-4xl font-bold text-white mb-12">Spaces</h1>
        <div className="w-full max-w-4xl mx-auto">
          {spaces.map(space => (
            <div key={space.id} className="bg-white shadow-lg rounded-lg mb-8 p-6 flex flex-col md:flex-row items-center">
              <img src={space.image} alt={space.name} className="w-40 h-40 object-cover rounded-lg md:mr-6 mb-4 md:mb-0" />
              <div className="flex flex-col flex-grow">
                <h2 className="text-xl font-bold mb-2">{space.name}</h2>
                <p className="text-gray-700 mb-1"><strong>Location:</strong> {space.location}</p>
                <p className="text-gray-700 mb-1"><strong>Capacity:</strong> {space.capacity}</p>
                <p className="text-gray-700 mb-1"><strong>Amenities:</strong> {space.amenities}</p>
                <p className="text-gray-700 mb-4"><strong>Ratecard:</strong> {space.ratecard}</p>
                <div className="flex space-x-4">
                  <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Update</button>
                  <button className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded">Delete</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Spaces;
