import React from 'react';
import { useLocation, Link } from 'react-router-dom';

const Invoice = () => {
  const location = useLocation();
  const { hours, amount, space } = location.state || { hours: 0, amount: 0, space: {} };

  if (!space) {
    return <div>Error: Space details are missing.</div>;
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-6 rounded-lg shadow-lg max-w-3xl w-full mx-4 sm:mx-6 lg:mx-8">
        <h2 className="text-2xl font-bold mb-4 text-center">Invoice</h2>
        <div className="space-y-4">
          <h3 className="text-xl font-bold mb-2">Space Details</h3>
          <p className="text-lg">Name: <span className="font-bold">{space.name}</span></p>
          <p className="text-lg">Location: <span className="font-bold">{space.location}</span></p>
          <p className="text-lg">Capacity: <span className="font-bold">{space.capacity}</span></p>
          <p className="text-lg">Amenities: <span className="font-bold">{space.amenities}</span></p>
          <p className="text-lg">Rate: <span className="font-bold">${space.rate}</span></p>

          <h3 className="text-xl font-bold mb-2">Booking Summary</h3>
          <p className="text-lg">Number of Hours: <span className="font-bold">{hours}</span></p>
          <p className="text-lg">Total Amount: <span className="font-bold">${amount}</span></p>
        </div>
        <div className="text-center mt-6">
          <Link to="/checkout" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
            Proceed to Checkout
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Invoice;
